/*
 * Colors
 */

:root {
 /* 
  * Color Theme Swatches
  */
 --primary-highlight: rgba(152, 193, 217, 1);
 --primary-shade: rgba(61, 90, 128, 1);
 --base: rgba(224, 251, 252, 1);
 --white: rgb(232, 246, 251);
 --secondary-highlight: rgba(238, 108, 77, 1);
 --secondary-shade: rgba(41, 50, 65, 1);

 --font-family: interstate, sans-serif;

}

* {
    box-sizing: border-box;
}

ul, li {
    margin: 0;
    padding: 0;
    list-style: none;
}

/*
 * Base Styles
 */

body {
    background-color: var(--white);
}

body > div { 
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    /* padding: 0 5vw; */
    margin: 0 auto;
    /* max-width: 1200px; */

    font-family: var(--font-family);
    font-weight: 400;
    font-style: normal;
}

body > div > header {
    background: white;
    display: flex;
    justify-content: space-between;
    padding: 2vw 5vw;
    border-bottom: 1px solid var(--primary-highlight);
}

body > div > main {
    width: 70%;
    padding: 2vw 2vw 0 5vw;
}

body > div > main > ul {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: space-between;
}

body > div > main > ul > li {
    display: flex;
    max-width: 49%;
    margin-bottom: 2ch;
}

body > div > main > ul > li > article {
    display: flex;
    flex-direction: column;

    width: 100%;
    padding: 2ch 5% 3ch;
    border: 1px solid var(--primary-highlight);
    background-color: white;
}

body > div > main > ul > li > article > header {
    display: flex;
    flex-direction: column;
}

figure {
    width: 100%;
    margin: 0;
}

img {
    width: 100%;
    height: auto;
    margin: 1ch 0;
}

header,
footer {
    width: 100%;
}

body > div > aside {
    flex: 1;
    width: 30%;
    padding: 2ch 10% 0 5ch;
}

aside > ul > li {
    margin-bottom: 2ch;
}

/*
 * Typography
 */

h1 {
    font-weight: 800;
    font-style: normal;
    font-size: 1.25rem;

    border-radius: 999px;

    /* background-color: var(--secondary-shade); */
    color: var(--primary-shade);

    /* display: inline-block; */
    /* padding: 1ch 3ch; */
    margin: 1ch 0;
}

h1 span {
    color: var(--primary-highlight);
    font-style: italic;
    font-weight: 200;
}

h2 {
    font-weight: 700;
    font-style: normal;
    color: var(--white);
}

header h4,
header h5 {
    margin: 0;
    padding: 0;
    text-transform: capitalize;
}

header h5 {
    font-size: .65rem;
    font-weight: 300;
}

/*
 * Interactions
 */

 button {
     cursor: pointer;
     background-color: var(--primary-highlight);
     font-size: 1.2rem;
     border: none;
     border-radius: 10px;
     padding: 1ch 2ch;
     border-bottom-width: 5px;
     border-bottom-style: solid;
     border-bottom-color: transparent;
     border-top-width: 5px;
     border-top-style: solid;
     border-top-color: transparent;
     -webkit-appearance: none;
        -moz-appearance: none;
             appearance: none;
 }

 button[aria-selected="true"] {
    background-color: var(--secondary-highlight);
 }

 button:not([aria-selected="true"]):hover {
    border-bottom-color: var(--primary-shade);
};
